package Loops;

//Print numbers from 1 to 50, but stop when you reach 25.

public class f20 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=50;i++)
		{
			if(i!=25)
			{
				System.out.println(" "+i);
			}
			else
			{
				break;
			}
		}

	}

}
